#include <iostream>#include <iomanip>using namespace std;void Nhap(int[], int&);void Xuat(int[], int);void LietKe(int[], int);int main(){	int b[100];	int k;	cout << "Nhap mang: ";	Nhap(b, k);	cout << "Mang ban dau: ";	Xuat(b, k);	cout << "Cac gia tri thoa dieu kien: ";	LietKe(b, k);	cout << "Ket thuc!!!";	return 0;}void Nhap(int a[], int& n){	cout << "Nhap n: ";	cin >> n;	srand(time(NULL));	for (int i = 0; i < n; i++)		a[i] = -100 + rand() / ((int)RAND_MAX / 200);}void Xuat(int a[], int n){	if (n == 0)		return;	Xuat(a, n - 1);	cout << fixed << setw(10) << setprecision(3) << a[n - 1];}void LietKe(int a[], int n){	if (n == 0)		return;	int dem = 0;	for (int i = n - 1; i >= 0 && a[i] % 2 == 0; i--)		dem++;	if (dem <= 1)	{		LietKe(a, n - 1);		return;	}	LietKe(a, n - dem);	for (int i = 0; i < dem; i++)		cout << setw(10) << a[n - dem + i];}